<template>
  <div class="offer">
    <sticky-offer @click="scrollToFooter" :close="close"></sticky-offer>
    <Header />
    <main class="main">
      <section id="summary" class="summary">
        <div class="summary__background">
          <img class="summary__bubble-1 summary__bubble" src="@/assets/bubble.svg" alt="bubble">
          <img class="summary__bubble-2 summary__bubble" src="@/assets/bubble.svg" alt="bubble">
        </div>
        <div class="summary__container">
          <h2 class="summary__title">Your profile summary</h2>
          <span class="summary__name">Jonas</span>
          <graph class="summary__graph"></graph>
        </div>
      </section>
      <section class="plan">
        <div class="plan__edge"></div>
        <div class="plan__container">
          <div class="plan__calendar calendar">
            <div class="calendar__wrapper">
              <img
                class="calendar__image"
                src="@/assets/landing/calendar.png"
                alt="calendar"
              />
              <span
                :class="{ active: pages }"
                class="calendar__page calendar__page_1"
              ></span>
              <span
                :class="{ active: pages }"
                class="calendar__page calendar__page_2"
              ></span>
              <span
                :class="{ active: pages }"
                class="calendar__page calendar__page_3"
              ></span>
            </div>
          </div>
          <div class="plan__description description">
            <h2 class="description__title">
              Become the best version of yourself!
            </h2>
            <ul class="description__list">
              <li class="description__item">
                Follow a step-by-step personalized plan
              </li>
              <li class="description__item">Create reading habit in 28 days</li>
              <li class="description__item">Achieve more with less effort</li>
            </ul>
          </div>
        </div>
      </section>
      <section id="steps" class="steps">
        <div class="steps__container">
          <h2 class="steps__title">How your plan was created</h2>
          <ul class="steps__list">
            <li :class="{ animate: animate }" class="steps__item">
              <div class="steps__img">
                <img src="@/assets/landing/books.png" alt="Books" />
              </div>
              <p class="steps__description">
                Hand-picked 28 books specially for you from 10,000 bestselling
                nonfiction books
              </p>
            </li>
            <li :class="{ animate: animate }" class="steps__item">
              <div class="steps__img">
                <img src="@/assets/landing/Summary.png" alt="Summary" />
              </div>
              <p class="steps__description">
                Created 15-min key insight summaries of bestselling nonfiction
                books
              </p>
            </li>
            <li :class="{ animate: animate }" class="steps__item">
              <div class="steps__img">
                <img src="@/assets/landing/Plan.png" alt="Plan" />
              </div>
              <p class="steps__description">
                Composed your personalized Self-Growth Plan
              </p>
            </li>
          </ul>
        </div>
      </section>
      <books-animation :pageY="pageY"></books-animation>
      <section id="step-by-step" class="step-by-step">
        <div class="step-by-step__container">
          <h2 class="step-by-step__title">
            Follow your plan step-by-step to get amazing results
          </h2>
          <ul class="step-by-step__list">
            <li
              :class="{ animate: animateSteps }"
              class="step-by-step__item step-by-step__item-1"
            >
              <img
                class="step-by-step__bubble"
                src="@/assets/landing/steps/step-1.svg"
                alt="step-1"
              />
              <div class="step-by-step__content">
                <div>
                  <span>1</span>
                  Step
                </div>
                <p class="">Check your<br />plan every day</p>
              </div>
            </li>
            <li
              :class="{ animate: animateSteps }"
              class="step-by-step__item step-by-step__item-2"
            >
              <img
                class="step-by-step__bubble"
                src="@/assets/landing/steps/step-2.svg"
                alt="step-2"
              />
              <div class="step-by-step__content">
                <div class="">
                  <span class="">2</span>
                  Step
                </div>
                <p class="">Read 15 min<br />summary</p>
              </div>
            </li>
            <li
              :class="{ animate: animateSteps }"
              class="step-by-step__item step-by-step__item-3"
            >
              <img
                class="step-by-step__bubble"
                src="@/assets/landing/steps/step-3.svg"
                alt="step-3"
              />
              <div class="step-by-step__content">
                <div class=""><span class="">3</span>Step</div>
                <p class="">Mark your<br />progress</p>
              </div>
            </li>
            <li
              :class="{ animate: animateSteps }"
              class="step-by-step__item step-by-step__item-4"
            >
              <img
                class="step-by-step__bubble"
                src="@/assets/landing/steps/step-4.svg"
                alt="step-4"
              />
              <div class="step-by-step__content">
                <div class=""><span class="">4</span>Step</div>
                <p class="">Celebrate a better<br />you</p>
              </div>
            </li>
          </ul>
        </div>
