import "../offer/Offer.scss";
export default {
  name: "Offer",
  components: {
    Graph,
    Header,
    StickyOffer,
    BooksAnimation,
    Footer,
  },
  data: () => ({
    pageY: 0,
    smallScroll: 0,
    benefitOpen: false,
    pages: false,
    animate: false,
    animateSteps: false,
    close: false,
    benefits: [
      {
        id: 0,
        title: "Higher life expectancy",
        icon: "https://emojipedia-us.s3.dualstack.us-west-1.amazonaws.com/thumbs/240/apple/271/smiling-face-with-sunglasses_1f60e.png",
        text: "Scientists from Yale University School of Public Health confirmed that people who read 3,5 hours per week are 23% likely to live longer.",
        show: false,
      },
      {
        id: 1,
        title: "Preventing mental diseases",
        icon: "https://emojipedia-us.s3.dualstack.us-west-1.amazonaws.com/thumbs/240/apple/271/face-with-head-bandage_1f915.png",
        text: "Scientists from the National Institute on Aging reported that reading helps to improve cognitive functioning and reduce dementia.",
        show: false,
      },
      {
        id: 2,
        title: "Better brain functioning",
        icon: "https://emojipedia-us.s3.dualstack.us-west-1.amazonaws.com/thumbs/240/apple/271/microscope_1f52c.png",
        text: "Scientists from California, the USA, proved that reading develops the network of signals in your brain throughout life and make it stronger and more advanced.",
        show: false,
      },
    ],
    questions: [
      {
        show: false,
        id: 0,
        title: "Why do I need a Self-Growth Plan?",
        text: "Psychologists claim that every habit starts with routine. In just two clicks Self-Growth Plan algorithms offer a personalized plan for you to become your better self. No additional efforts needed!",
      },
      {
        show: false,
        id: 1,
        title: "How long will I wait for my plan?",
        text: "There is no need to delay your self-improvement! Your plan will be in your email inbox right after your order placement. In case you haven’t found your plan, please, check Spam or contact us via support@selfgrowthplan.com",
      },
      {
        show: false,
        id: 2,
