        text: "There are no deadlines in terms of your access. Even after you finish your 28-days-journey, all the insights stay with you forever!",
      },
    ],
  }),
  methods: {
    fetchData() {
      fetch(
        "https://sheet.best/api/sheets/e3738555-6463-4b70-81a2-7e80fb80e847"
      )
        .then((response) => response.json())
        .then((data) => {
          console.log(data);
        })
        .catch((error) => {
          console.error(error);
        });
    },
    scroll(e) {
      this.pageY = e.target.documentElement.scrollTop;
      this.smallScroll = e.target.documentElement.scrollTop / 20;
      if (this.pageY >= this.getCoordinatesY('summary')+100) {
        this.pages = true;
      } else {
        this.pages = false;
      }
      if (this.pageY >= this.getCoordinatesY('steps')-300) {
        this.animate = true;
      } else {
        this.animate = false;
      }
      if (this.pageY >= this.getCoordinatesY('step-by-step')-500) {
        this.animateSteps = true;
      } else {
        this.animateSteps = false;
      }
      // console.log(this.smallScroll);
      // console.log(document.body.scrollHeight);
      // console.log(e.target.documentElement.scrollTop);
      if (this.pageY <= 3900) {
        this.close = false;
      } else {
        this.close = true;
      }
    },
    show(arr, key) {
      arr[key].show = !arr[key].show;
    },
    scrollToFooter() {
      window.scrollTo({
        top: this.getCoordinatesY('block') - 50,
        left: 0,
        behavior: "smooth",
      });
      this.close = true;
    },
    getCoordinatesY(id){
      let block = document.getElementById(id).getBoundingClientRect();
      return block.y + pageYOffset;
    },
  },
  computed:{
    
  },
  created(){
    window.scrollTo({
      top: 0,
      left: 0,
      behavior: "smooth",
    });
  },
  mounted() {
    window.addEventListener("scroll", this.scroll);
    
    // this.fetchData();
  },
  beforeUnmount() {
    window.removeEventListener("scroll", this.scroll);
  },
